SELECT * FROM (SELECT MONTH(DATE) as month, ROUND(SUM(money),2) as total_money FROM `trans` WHERE `items` LIKE '%BLDDD%' and YEAR(DATE) = '2021' GROUP BY MONTH(DATE) order by MONTH(DATE) DESC) as t1 FULL OUTER JOIN (SELECT MONTH(DATE) as both_month, ROUND(SUM(money),2) as total_money FROM `trans` WHERE `items` LIKE '%BLDDD%' and person = 'Both' and YEAR(DATE) = '2021' GROUP BY MONTH(DATE) order by MONTH(DATE) DESC) as t2 ON t1.month = t2.both_month
